mapper.insert:
INSERT INTO jobposting (APLNUM ,POST_ID 
                       ,RECRUITNUM,DEADLINE 
                       ,DUTY,WORKSPACE 
                       ,SALARY,DEPARTMENT
                       ,CAREER,EDU
                       ,SKILLS,LICENSES) 
VALUES ( (SELECT NVL(MAX(APLNUM),0)+1 FROM JOBPOSTING ) ,#{post_id} 
          ,#{recruitnum} ,#{deadline} 
          ,#{duty} ,#{workspace} 
          ,#{salary} ,#{department}
          ,#{edu},#{career}
          ,#{skills},#{licenses})

mapper.update:
UPDATE 
       JOBPOSTING
 SET    
       POST_ID     = #{post_id}
      ,RECRUITNUM  = #{recruitnum}
      ,WORKSPACE   = #{workspace} 
      ,DEADLINE    = #{deadline}
      ,SALARY      = #{salary}
      ,DEPARTMENT  = #{department}
      ,SKILLS      = #{skills}
      ,LICENSES    = #{licenses}
      ,CAREER      = #{career}
      ,EDU         = #{edu}
      ,DUTY        = #{duty}
WHERE 
     APLNUM       = #{aplnum}

UPDATE JOBPOSTING
   SET HIT = HIT+1
   WHERE APLNUM= #{aplnum}
